package Homework1;

public class QuotientRemainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a,b,quo,rem;
		
		a=20;
		b=10;
		quo=a%b;
		rem=a/b;
		
		System.out.println("a = "+a);
		System.out.println("b = "+b);
		System.out.println("quo = "+quo);
		System.out.println("rem = "+rem);
	
	}

}
